import { Injectable } from '@angular/core';
import { Constants } from '../constants';
import { Observable } from 'rxjs';
import { HttpClient, HttpHeaders } from '@angular/common/http';

import { movie } from '../models/movie';
import { User } from '../models/user';

@Injectable({
  providedIn: 'root'
})
export class MovieService {
  private URL : String = 'http://localhost:3000';//+Constants.API_VERSION

  constructor(private http: HttpClient) {}

  searchMovie(title : string) : Observable<any>{//changed string to m[ovie]
    //will need to turn returned get data into movie
    let url = this.URL+`/search/`+title;
    console.log(url);
    var ans = this.http.get<any>(url);
    console.log("SEARCHED=",ans);
    return ans;
  }

  /*
  moviefy(data:any) : movie{
    return MOVIE
  }
  */

  login( userName : String, password : String  ) : Observable<User> {
    let headers = { "Content-Type": "application/x-www-form-urlencoded"};
    return this.http.post<User>(this.URL + "/login", "userName=" + userName + "&password=" + password, {headers} );
  }

  update() : Observable<User> {
    let headers = { "Content-Type": "application/x-www-form-urlencoded "};
    return this.http.post<User>(this.URL+"/defaults", "", {headers});
  }
}
